import {useState} from "react";
import * as PropTypes from "prop-types";

function Fragment(props) {
  return null;
}

Fragment.propTypes = { children: PropTypes.node };
const BookList = (props) => {

    const [bookList, setBookLists] = useState([]);
    let bookLists;

    if(bookList){
        bookLists = bookList.map(book =>
            <div key={book.bookId}>
                <h3>{book.bookName}</h3>
                <p>{book.bookAuthor} 저 ({book.bookPublishedYear}년)</p>
                <p>서지 관리 ID : {book.bookId}</p>
                <div>
                    <button>삭제</button>
                    <button>자세히</button>
                </div>
            </div>
        )
    }

    return(
        <div>
            <h3>화면 조작 패널</h3>
            <p>현재 등록되어 있는 서지 정보는 {}개입니다.</p>
            <h3>특정 연도만 확인하기</h3>
            <Fragment>
                {bookLists}
            </Fragment>
        </div>
    );
}

export default BookList;
