import React, {useState} from "react";

const Register = (props) => {

    const [userInput, setUserInput] = useState({
        bookName: '',
        bookIntro: '',
        bookPublishedYear : 0,
        bookAuthor: '',
    });

    const submitHandler = (event) =>{
        event.preventDefault();

    }

    const bookNameChangeHandler = (event) => {
        setUserInput({
            ...userInput,
            bookName:event.target.value,
        });
    }
    const bookIntroChangeHandler = (event) =>{
        setUserInput({
            ...userInput,
            bookIntro: event.target.value,
        });
    }

    const bookPublishYearChangeHandler = (event) =>{
        setUserInput({
            ...userInput,
            bookPublishedYear: event.target.value,
        });
    }

    const bookAuthorChangeHandler = (event)=>{
        setUserInput({
            ...userInput,
            bookAuthor: event.target.value,
        });
    }
    return(
        <div>
            <h3>새로운 책 등록</h3>
            <form onSubmit={submitHandler}>
                <div>
                    <label>책 이름</label>
                    <input type="text" required />
                </div>

                <div>
                    <label>책 한줄 소개</label>
                    <input type="text"/>
                </div>

                <div>
                    <label>출간 연도</label>
                    <input type="text"/>
                </div>
                <div>
                    <label>저자</label>
                    <input type="text"/>
                </div>
                <p>{}</p>
                <button>초기화</button>
                <button>등록</button>
            </form>
        </div>
    );
}

export default Register;
