import {useParams} from "react-router-dom";

const Book = (props) =>{
    const params = useParams();
    const bookId = params.bookId;

    const bookList = props.data;
    let book;

    book = bookList.filter(book=>
        book.bookId === bookId
    );

    return(
        <div>
            <h3>{props.book.bookName}</h3>
            <ul>
                <li>저자: {props.book.bookAuthor}</li>
                <li>출간년도: {props.book.bookPublishedYear}</li>
            </ul>
            <p>소개글</p>
            <p>{props.book.bookIntro}</p>
            <p>{props.book.bookAuthor}, {props.book.bookName}, {props.book.bookPublishedYear}</p>
        </div>
    );
}

export default Book;
